// #Sireum

package base.runtimemonitor

import org.sireum._
import base._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun

object GumboXDispatcher {
  def checkContract(observationKind: ObservationKind.Type, preContainer: Option[art.DataContent], postContainer: Option[art.DataContent]): B = {
    observationKind match {
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_postInit =>
        // checking the post-state values of ArduPilot's initialise entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_preCompute =>
        // checking the pre-state values of ArduPilot's compute entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_postCompute =>
        // checking the post-state values of ArduPilot's compute entrypoint is not required
        return T

      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_postInit =>
        // checking the post-state values of Firewall's initialise entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_preCompute =>
        // checking the pre-state values of Firewall's compute entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute =>
        val result: B = base.SW.Firewall_Impl_seL4_Firewall_Firewall_GumboX.compute_CEP_Post_Container(preContainer.get.asInstanceOf[base.SW.Firewall_Impl_seL4_Firewall_Firewall_PreState_Container_PS], postContainer.get.asInstanceOf[base.SW.Firewall_Impl_seL4_Firewall_Firewall_PostState_Container_PS])
        //println(s"Firewall.timeTriggered: Post-condition: ${if (result) "" else "un"}satisfied")
        return result

      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_postInit =>
        // checking the post-state values of LowLevelEthernetDriver's initialise entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_preCompute =>
        // checking the pre-state values of LowLevelEthernetDriver's compute entrypoint is not required
        return T
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_postCompute =>
        // checking the post-state values of LowLevelEthernetDriver's compute entrypoint is not required
        return T

      case _ => halt("Infeasible")
    }
  }

  def genTestSuite(testCases: ISZ[(Z, ISZ[ST])]): Unit = {
    val tq = "\"\"\""

    val testRoot = Os.path(".") / "src" / "test" / "bridge"

    val ArduPilot_Impl_seL4_ArduPilot_ArduPilot_id = Arch.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot.id
    val Firewall_Impl_seL4_Firewall_Firewall_id = Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall.id
    val LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_id = Arch.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver.id

    def genUniqueSuiteName(path: Os.Path, prefix: String): String = {
      var i = 0
      while(true) {
        val cand = path / s"${prefix}_${i}.scala"
        if (!cand.exists) {
          return s"${prefix}_${i}"
        }
        i = i + 1
      }
      halt("Infeasible")
    }

    for (p <- testCases) {
      art.Art.BridgeId.fromZ(p._1) match {
        case ArduPilot_Impl_seL4_ArduPilot_ArduPilot_id =>
          val prefix = "ArduPilot_Impl_seL4_ArduPilot_ArduPilot_RM_TestSuite"
          val path = testRoot /+ ISZ("base","SW")
          val suiteName = genUniqueSuiteName(path, prefix)

          val testSuite =
            st"""package base.SW
                |
                |import org.sireum._
                |import base.SW._
                |
                |class ${suiteName} extends ArduPilot_Impl_seL4_ArduPilot_ArduPilot_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = T
                |
                |  ${p._2}
                |}"""
          val filename = path / s"${suiteName}.scala"
          filename.writeOver(testSuite.render)
          println(s"Wrote: ${filename.toUri}")
        case Firewall_Impl_seL4_Firewall_Firewall_id =>
          val prefix = "Firewall_Impl_seL4_Firewall_Firewall_RM_TestSuite"
          val path = testRoot /+ ISZ("base","SW")
          val suiteName = genUniqueSuiteName(path, prefix)

          val testSuite =
            st"""package base.SW
                |
                |import org.sireum._
                |import base.SW._
                |
                |class ${suiteName} extends Firewall_Impl_seL4_Firewall_Firewall_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = T
                |
                |  ${p._2}
                |}"""
          val filename = path / s"${suiteName}.scala"
          filename.writeOver(testSuite.render)
          println(s"Wrote: ${filename.toUri}")
        case LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_id =>
          val prefix = "LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_RM_TestSuite"
          val path = testRoot /+ ISZ("base","SW")
          val suiteName = genUniqueSuiteName(path, prefix)

          val testSuite =
            st"""package base.SW
                |
                |import org.sireum._
                |import base.SW._
                |
                |class ${suiteName} extends LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_GumboX_TestHarness_ScalaTest {
                |  val verbose: B = T
                |
                |  ${p._2}
                |}"""
          val filename = path / s"${suiteName}.scala"
          filename.writeOver(testSuite.render)
          println(s"Wrote: ${filename.toUri}")
        case x => halt(s"Infeasible bridge id: $x")
      }
    }
  }

  def genTestCase(observationKind: ObservationKind.Type, preContainer: Option[String], postContainer: Option[String], testNameSuffix: Option[String]): ST = {
    val tq = "\"\"\""
    val suffix: String =
      if (testNameSuffix.nonEmpty) testNameSuffix.get
      else ""

    observationKind match {

      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute =>
        return (st"""// Begin test cases for ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute
                    |
                    |test(s"ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute: Check Post-condition$suffix") {
                    |  val preJson: String = st${tq}${preContainer.get}${tq}.render
                    |  val postJson: String = st${tq}${postContainer.get}${tq}.render
                    |  val preContainer = base.JSON.toSWFirewall_Impl_seL4_Firewall_Firewall_PreState_Container_PS(preJson).left
                    |  val postContainer = base.JSON.toSWFirewall_Impl_seL4_Firewall_Firewall_PostState_Container_PS(postJson).left
                    |  if (verbose) {
                    |    println("Pre-State Values:")
                    |    println(s"  $$preContainer")
                    |    println("Post-State Values:")
                    |    println(s"  $$postContainer");
                    |  }
                    |  assert(base.SW.Firewall_Impl_seL4_Firewall_Firewall_GumboX.compute_CEP_Post_Container(preContainer, postContainer))
                    |}
                    |// End test cases for ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute""")

      case _ => return st"// TODO ${observationKind}"
    }
  }

  def getUpdates(bridge_id: art.Art.BridgeId, observationKind: ObservationKind.Type, container: art.DataContent): Map[String, String] = {
    observationKind match {
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_postInit =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.ArduPilot_Impl_seL4_ArduPilot_ArduPilot_PostState_Container_PS]
        if (postContainer.api_EthernetFramesTx.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesTx" ~> postContainer.api_EthernetFramesTx.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_postInit =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.Firewall_Impl_seL4_Firewall_Firewall_PostState_Container_PS]
        if (postContainer.api_EthernetFramesRxOut.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesRxOut" ~> postContainer.api_EthernetFramesRxOut.get.string
        }
        if (postContainer.api_EthernetFramesTxOut.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesTxOut" ~> postContainer.api_EthernetFramesTxOut.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_postInit =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_PostState_Container_PS]
        if (postContainer.api_EthernetFramesRx.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesRx" ~> postContainer.api_EthernetFramesRx.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_preCompute =>
        var updates: Map[String, String] = Map.empty
        val preContainer = container.asInstanceOf[base.SW.ArduPilot_Impl_seL4_ArduPilot_ArduPilot_PreState_Container_PS]
        if (preContainer.api_EthernetFramesRx.nonEmpty) {
          updates = updates + s"${bridge_id}_In_EthernetFramesRx" ~> preContainer.api_EthernetFramesRx.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_preCompute =>
        var updates: Map[String, String] = Map.empty
        val preContainer = container.asInstanceOf[base.SW.Firewall_Impl_seL4_Firewall_Firewall_PreState_Container_PS]
        if (preContainer.api_EthernetFramesRxIn.nonEmpty) {
          updates = updates + s"${bridge_id}_In_EthernetFramesRxIn" ~> preContainer.api_EthernetFramesRxIn.get.string
        }
        if (preContainer.api_EthernetFramesTxIn.nonEmpty) {
          updates = updates + s"${bridge_id}_In_EthernetFramesTxIn" ~> preContainer.api_EthernetFramesTxIn.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_preCompute =>
        var updates: Map[String, String] = Map.empty
        val preContainer = container.asInstanceOf[base.SW.LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_PreState_Container_PS]
        if (preContainer.api_EthernetFramesTx.nonEmpty) {
          updates = updates + s"${bridge_id}_In_EthernetFramesTx" ~> preContainer.api_EthernetFramesTx.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_ArduPilot_ArduPilot_postCompute =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.ArduPilot_Impl_seL4_ArduPilot_ArduPilot_PostState_Container_PS]
        if (postContainer.api_EthernetFramesTx.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesTx" ~> postContainer.api_EthernetFramesTx.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_Firewall_Firewall_postCompute =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.Firewall_Impl_seL4_Firewall_Firewall_PostState_Container_PS]
        if (postContainer.api_EthernetFramesRxOut.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesRxOut" ~> postContainer.api_EthernetFramesRxOut.get.string
        }
        if (postContainer.api_EthernetFramesTxOut.nonEmpty) {
          updates = updates + s"${bridge_id}_Out_EthernetFramesTxOut" ~> postContainer.api_EthernetFramesTxOut.get.string
        }
        return updates
      case base.runtimemonitor.ObservationKind.ZCU102_Impl_Instance_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_postCompute =>
        var updates: Map[String, String] = Map.empty
        val postContainer = container.asInstanceOf[base.SW.LowLevelEthernetDriver_Impl_seL4_LowLevelEthernetDriver_LowLevelEthernetDriver_PostState_Container_PS]
        if (postContainer.api_EthernetFramesRx.nonEmpty) {
