// #Sireum

package base.SW

import org.sireum._
import art.Art
import base._

// Do not edit this file as it will be overwritten if HAMR codegen is rerun
@msig trait Firewall_Impl_seL4_Firewall_Firewall_TestApi {

  def BeforeEntrypoint(): Unit = {
    Art.initTest(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall)
  }

  def AfterEntrypoint(): Unit = {
    Art.finalizeTest(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall)
  }

  def testCompute(): Unit = {
    Art.manuallyClearOutput()
    Art.testCompute(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall)
  }

  def testInitialise(): Unit = {
    Art.manuallyClearOutput()
    Art.testInitialise(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall)
  }

  /** helper function to set the values of all input ports.
   * @param EthernetFramesRxIn payloads for event data port EthernetFramesRxIn.
   *   ART currently supports single element event data queues so
   *   only the last element of EthernetFramesRxIn will be used
   * @param EthernetFramesTxIn payloads for event data port EthernetFramesTxIn.
   *   ART currently supports single element event data queues so
   *   only the last element of EthernetFramesTxIn will be used
   */
  def put_concrete_inputs(EthernetFramesRxIn : ISZ[SW.StructuredEthernetMessage_i],
                          EthernetFramesTxIn : ISZ[SW.StructuredEthernetMessage_i]): Unit = {
    for(v <- EthernetFramesRxIn){
      put_EthernetFramesRxIn(v)
    }
    for(v <- EthernetFramesTxIn){
      put_EthernetFramesTxIn(v)
    }
  }


  /** helper function to check Firewall_Impl_seL4_Firewall_Firewall's
   * output ports.  Use named arguments to check subsets of the output ports.
   * @param EthernetFramesRxOut method that will be called with the payloads to be sent
   *        on the outgoing event data port 'EthernetFramesRxOut'.
   * @param EthernetFramesTxOut method that will be called with the payloads to be sent
   *        on the outgoing event data port 'EthernetFramesTxOut'.
   */
  def check_concrete_output(EthernetFramesRxOut: ISZ[SW.StructuredEthernetMessage_i] => B,
                            EthernetFramesTxOut: ISZ[SW.StructuredEthernetMessage_i] => B): Unit = {
    var testFailures: ISZ[ST] = ISZ()

    var EthernetFramesRxOutValue: ISZ[SW.StructuredEthernetMessage_i] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_EthernetFramesRxOut().nonEmpty) { EthernetFramesRxOutValue = EthernetFramesRxOutValue :+ get_EthernetFramesRxOut().get }
    if(!EthernetFramesRxOut(EthernetFramesRxOutValue)) {
      testFailures = testFailures :+ st"'EthernetFramesRxOut' did not match expected: received ${EthernetFramesRxOutValue.size} events with the following payloads ${EthernetFramesRxOutValue}"
    }
    var EthernetFramesTxOutValue: ISZ[SW.StructuredEthernetMessage_i] = ISZ()
    // TODO: event data port getter should return all of the events/payloads
    //       received on event data ports when queue sizes > 1 support is added
    //       to ART
    if(get_EthernetFramesTxOut().nonEmpty) { EthernetFramesTxOutValue = EthernetFramesTxOutValue :+ get_EthernetFramesTxOut().get }
    if(!EthernetFramesTxOut(EthernetFramesTxOutValue)) {
      testFailures = testFailures :+ st"'EthernetFramesTxOut' did not match expected: received ${EthernetFramesTxOutValue.size} events with the following payloads ${EthernetFramesTxOutValue}"
    }

    assert(testFailures.isEmpty, st"${(testFailures, "\n")}".render)
  }


  // setter for in EventDataPort
  def put_EthernetFramesRxIn(value : SW.StructuredEthernetMessage_i): Unit = {
    Art.insertInInfrastructurePort(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall.operational_api.EthernetFramesRxIn_Id, SW.StructuredEthernetMessage_i_Payload(value))
  }

  // setter for in EventDataPort
  def put_EthernetFramesTxIn(value : SW.StructuredEthernetMessage_i): Unit = {
    Art.insertInInfrastructurePort(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall.operational_api.EthernetFramesTxIn_Id, SW.StructuredEthernetMessage_i_Payload(value))
  }

  // getter for out EventDataPort
  def get_EthernetFramesRxOut(): Option[SW.StructuredEthernetMessage_i] = {
    val value: Option[SW.StructuredEthernetMessage_i] = get_EthernetFramesRxOut_payload() match {
      case Some(SW.StructuredEthernetMessage_i_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port EthernetFramesRxOut.  Expecting 'SW.StructuredEthernetMessage_i_Payload' but received ${v}")
      case _ => None[SW.StructuredEthernetMessage_i]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_EthernetFramesRxOut_payload(): Option[SW.StructuredEthernetMessage_i_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall.initialization_api.EthernetFramesRxOut_Id).asInstanceOf[Option[SW.StructuredEthernetMessage_i_Payload]]
  }

  // getter for out EventDataPort
  def get_EthernetFramesTxOut(): Option[SW.StructuredEthernetMessage_i] = {
    val value: Option[SW.StructuredEthernetMessage_i] = get_EthernetFramesTxOut_payload() match {
      case Some(SW.StructuredEthernetMessage_i_Payload(v)) => Some(v)
      case Some(v) => halt(s"Unexpected payload on port EthernetFramesTxOut.  Expecting 'SW.StructuredEthernetMessage_i_Payload' but received ${v}")
      case _ => None[SW.StructuredEthernetMessage_i]()
    }
    return value
  }

  // payload getter for out EventDataPort
  def get_EthernetFramesTxOut_payload(): Option[SW.StructuredEthernetMessage_i_Payload] = {
    return Art.observeOutInfrastructurePort(Arch.ZCU102_Impl_Instance_seL4_Firewall_Firewall.initialization_api.EthernetFramesTxOut_Id).asInstanceOf[Option[SW.StructuredEthernetMessage_i_Payload]]
  }

}
